
-- DataBase LibreriaJacaranda

-- Users table

CREATE TABLE users
(
userCod VARCHAR(20),
pasword	VARCHAR(10),

CONSTRAINT pk_users PRIMARY KEY (userCod)
);


CREATE TABLE articles (
isbn VARCHAR(50),
title VARCHAR(50),
author VARCHAR(50),
published_date DATE,
quantity INTEGER,
price DECIMAL(5,2),
stock TINYINT(1),

CONSTRAINT pk_articles PRIMARY KEY (isbn)
);


